import { PERSONS_ACTIONS } from './../actions/person.actions';
import { createReducer, on } from "@ngrx/store";
import { initialPersonsState } from '../states/person.state';
export const PersonsReducer = createReducer(initialPersonsState, on(PERSONS_ACTIONS.load.run, state => {
  return Object.assign(Object.assign({}, state), {
    loading: true
  });
}), on(PERSONS_ACTIONS.load.success, (state, {
  persons
}) => {
  return Object.assign(Object.assign({}, state), {
    loading: false,
    loaded: true,
    persons
  });
}), on(PERSONS_ACTIONS.load.failed, state => {
  return Object.assign(Object.assign({}, state), {
    loading: false
  });
}));
